def select_neighbor(archive: List[Tuple[np.ndarray, Tuple[float, float]]], instance: np.ndarray, distance_matrix_1: np.ndarray, distance_matrix_2: np.ndarray) -> np.ndarray:
    if not archive:
        raise ValueError("Archive is empty")

    # Select solutions based on Pareto dominance and diversity
    objectives = np.array([obj for _, obj in archive])
    pareto_front = []
    for i, obj in enumerate(objectives):
        dominated = False
        for j, other_obj in enumerate(objectives):
            if i != j and (other_obj[0] <= obj[0] and other_obj[1] <= obj[1]) and (other_obj[0] < obj[0] or other_obj[1] < obj[1]):
                dominated = True
                break
        if not dominated:
            pareto_front.append(i)

    if pareto_front:
        # Select from Pareto front with diversity
        pareto_objectives = objectives[pareto_front]
        diversity_scores = np.sum((pareto_objectives - np.mean(pareto_objectives, axis=0)) ** 2, axis=1)
        selected_idx = pareto_front[np.argmax(diversity_scores)]
    else:
        # Select from all solutions with diversity
        diversity_scores = np.sum((objectives - np.mean(objectives, axis=0)) ** 2, axis=1)
        selected_idx = np.argmax(diversity_scores)

    selected_solution = archive[selected_idx][0].copy()
    new_solution = selected_solution.copy()
    n = len(new_solution)

    # Calculate Pareto dominance score
    dominated_count = 0
    for other_obj in objectives:
        if (other_obj[0] <= objectives[selected_idx][0] and other_obj[1] <= objectives[selected_idx][1]) and (other_obj[0] < objectives[selected_idx][0] or other_obj[1] < objectives[selected_idx][1]):
            dominated_count += 1
    dominance_score = dominated_count / len(objectives)

    # Determine relocation range based on dominance score
    reloc_range = max(2, min(n // 3, int(np.ceil(n * 0.2 * (1 + dominance_score * np.random.rand())))))
    pos = np.random.randint(0, n)
    start = max(0, pos - reloc_range)
    end = min(n, pos + reloc_range)

    # Hybrid operator: edge insertion + probabilistic relocation
    if np.random.rand() < 0.7:
        # Edge insertion
        i, j = np.random.randint(start, end, size=2)
        if i != j:
            new_solution = np.concatenate([new_solution[:i], new_solution[j:j+1], new_solution[i+1:j], new_solution[i:i+1], new_solution[j+1:]])
    else:
        # Probabilistic relocation
        i = np.random.randint(start, end)
        j = np.random.randint(0, n)
        if i != j:
            new_solution = np.concatenate([new_solution[:i], new_solution[i+1:j], new_solution[i:i+1], new_solution[j:]])

    # Ensure the tour is circular
    if new_solution[0] != new_solution[-1]:
        new_solution = np.append(new_solution, new_solution[0])

    return new_solution
