def select_neighbor(archive: List[Tuple[np.ndarray, Tuple[float, float]]], instance: np.ndarray, distance_matrix_1: np.ndarray, distance_matrix_2: np.ndarray) -> np.ndarray:
    if not archive:
        raise ValueError("Archive is empty")

    # Select solution with highest combined objective value
    selected = max(archive, key=lambda x: sum(x[1]))[0].copy()
    new_solution = selected.copy()
    n = len(new_solution)

    if n < 4:
        return new_solution

    # Multi-segment inversion with biased edge swapping
    for _ in range(3):
        # Select 3 random segments
        points = sorted(random.sample(range(n), 3))
        i, j, k = points[0], points[1], points[2]

        # Invert middle segment with probability based on edge diversity
        if random.random() < 0.6:
            new_solution[j:k] = new_solution[j:k][::-1]

        # Swap edges between segments with probability based on objective trade-off
        if random.random() < 0.4:
            if k - j > 1:
                new_solution[j], new_solution[j+1] = new_solution[j+1], new_solution[j]

        # Ensure feasibility
        if len(np.unique(new_solution)) != n:
            new_solution = selected.copy()

    return new_solution

